        public static readonly string BASIC_MAIL_HTML_REPORT_TEMPLATE = "\r\n<!DOCTYPE html>\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xmlns:v=\"urn:schemas-microsoft-com:vml\" xmlns:o=\"urn:schemas-microsoft-com:office:office\">\r\n<head>\r\n    <title>TAS SYSTEM</title><!--[if !mso]><!-- -->\r\n    <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\"><!--<![endif]-->\r\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\r\n    <style type=\"text/css\">\r\n        #outlook a {\r\n            padding: 0;\r\n        }\r\n\r\n        .ReadMsgBody {\r\n            width: 100%;\r\n        }\r\n\r\n        .ExternalClass {\r\n            width: 100%;\r\n        }\r\n\r\n            .ExternalClass * {\r\n                line-height: 100%;\r\n            }\r\n\r\n        body {\r\n            margin: 0;\r\n            padding: 0;\r\n            -webkit-text-size-adjust: 100%;\r\n            -ms-text-size-adjust: 100%;\r\n        }\r\n\r\n        table, td {\r\n            border-collapse: collapse;\r\n            mso-table-lspace: 0pt;\r\n            mso-table-rspace: 0pt;\r\n        }\r\n\r\n        img {\r\n            border: 0;\r\n            height: auto;\r\n            line-height: 100%;\r\n            outline: none;\r\n            text-decoration: none;\r\n            -ms-interpolation-mode: bicubic;\r\n        }\r\n\r\n        p {\r\n            display: block;\r\n            margin: 13px 0;\r\n        }\r\n    </style><!--[if !mso]><!-->\r\n    <style type=\"text/css\">\r\n        @media only screen and (max-width:480px) {\r\n            @-ms-viewport {\r\n                width: 320px;\r\n            }\r\n\r\n            @viewport {\r\n                width: 320px;\r\n            }\r\n        }\r\n    </style><!--<![endif]--><!--[if mso]><xml><o:OfficeDocumentSettings><o:AllowPNG/><o:PixelsPerInch>96</o:PixelsPerInch></o:OfficeDocumentSettings></xml><![endif]--><!--[if lte mso 11]><style type=\"text/css\">.outlook-group-fix { width:100% !important; }</style><![endif]-->\r\n    <style type=\"text/css\">\r\n        @media only screen and (min-width:480px) {\r\n            .mj-column-per-100 {\r\n                width: 100% !important;\r\n            }\r\n        }\r\n    </style>\r\n    <style type=\"text/css\"></style>\r\n</head>\r\n<body style=\"background-color:#f9f9f9;\">\r\n    <div style=\"background-color:#f9f9f9;\">\r\n        <div style=\"background:#f9f9f9;background-color:#f9f9f9;Margin:0px auto;max-width:600px;\">\r\n            <table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" role=\"presentation\" style=\"background:#f9f9f9;background-color:#f9f9f9;width:100%;\">\r\n                <tbody>\r\n                    <tr>\r\n                        <td style=\"border-bottom:#fd7e14 solid 5px;direction:ltr;font-size:0px;padding:20px 0;text-align:center;vertical-align:top;\">\r\n                        </td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n        </div>\r\n        <div style=\"background:#fff;background-color:#fff;Margin:0px auto;max-width:600px;\">\r\n            <table align=\"center\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\" role=\"presentation\" style=\"background:#fff;background-color:#fff;width:100%;\">\r\n                <tbody>\r\n                    <tr>\r\n                        <td style=\"border:#dddddd solid 1px;border-top:0px;direction:ltr;font-size:0px;padding:20px 0;text-align:center;vertical-align:top;\">\r\n                            <div class=\"mj-column-per-100 outlook-group-fix\" style=\"font-size:13px;text-align:left;direction:ltr;display:inline-block;vertical-align:bottom;width:100%;\">\r\n                                <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" role=\"presentation\" style=\"vertical-align:bottom;\" width=\"100%\">\r\n                                    <tr>\r\n\r\n                    </tr>\r\n                    <tr>\r\n\t\t\t\t\t<td align=\"left\" style=\"padding:0px 25px;word-break:break-word;font-family: sans-serif,Arial,sans-serif;\">\r\n                            <b>Dear Team</b>, <br><br>\r\n\r\n\t\t\t\t\t\t\t\tPlease find attached the latest report generated by <b>OTTAS</b> as part of our automated reporting services </br></br>\r\n\t\t\t\t\t\t\t\t\r\n\t\t\t\t\t\t\t\t<b>Report Details: </b></br>\r\n\t\t\t\t\t\t\t\t<ul>\r\n\t\t\t\t\t\t\t\t\t<li><b>Report Name : </b> {reporttemplate}</li>\r\n\t\t\t\t\t\t\t\t\t<li><b>Job Code : </b> {ReportJobCode}</li>\r\n\t\t\t\t\t\t\t\t\t<li><b>Execution Date : </b> {date}</li>\r\n\t\t\t\t\t\t\t\t\t\r\n\t\t\t\t\t\t\t\t</ul>\r\n\t\t\t\t\t\t\t\t\r\n\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\t\r\n\t\t\t\t\t\t\t\t</br>\r\n\r\n\t\t\t\t\t\t\t\t</br> </br>\r\n\t\t\t\t\t\t\t\t\r\n\t\t\t\t\t\t\t\t\r\n                        </td>\r\n\t\t\t\t\t\t\t\r\n\t\t\t\t\t\t\t\r\n                        </td>\r\n                    </tr>\r\n                    </tr>\r\n                    <tr>\r\n                        <td align=\"right\" style=\"color: #00AAAD;font-style: italic; padding:0px 25px;word-break:break-word;font-family: 'Exo 2', sans-serif,Arial,sans-serif;\">\r\n                            This is an automatically by the TAS system generated email – please do not reply to it.\r\n                        </td>\r\n                    </tr>\r\n            </table>\r\n        </div>\r\n        </td>\r\n        </tr>\r\n        </tbody>\r\n        </table>\r\n    </div>\r\n    <div style=\"Margin:0px auto;max-width:600px;\"></div>\r\n    </div>\r\n</body>\r\n</html>\r\n";

        public static readonly string REPORT_TRANSPORT_MANIFEST_QUERY = "\r\nSELECT   CONCAT(\r\n  \r\n    CASE\r\n    WHEN gender = 1 THEN 'Mr.'\r\n    ELSE 'Mrs.'\r\n  END,\r\n  ' ',\r\n  \r\n  \r\n  e.Firstname,  ' ', e.Lastname) PassengerName,\r\n  ee.Description Company,\r\n  d.Name Department,\r\n  p.Description Position,\r\n  CONCAT(cc.Code, '-', cc.Description) CostCenter,\r\n  '' Signatures,\r\n  '' AS CNo, e.PickUpAddress,\r\n  t.Status\r\n from Transport t\r\nleft join Employee e ON t.EmployeeId = e.Id\r\nleft JOIN Employer ee ON t.employerId = ee.Id\r\nleft JOIN Department d ON e.DepartmentId = d.Id\r\nleft JOIN Position p  ON e.PositionId = p.Id\r\nleft JOIN CostCodes cc ON e.CostCodeId = cc.Id\r\n  WHERE t.ScheduleId IN (SELECT Id \r\nFROM TransportSchedule\r\nWHERE  t.ScheduleId ={scheduleId} \r\n) ORDER BY t.Status;\r\n";
        public static readonly string REPORT_WORKFLOW_CANCELLED_DECLINED_QUERY = "\r\nSELECT \r\n  rd.id, \r\n  rd.DocumentType, \r\n  rd.CurrentAction, \r\n  CONCAT(e.Firstname, ' ', e.Lastname) AS Subject,\r\n  CONCAT(ee.Firstname, ' ', ee.Lastname) AS Requester,\r\n  rg.Description AS Step,\r\n  rhist.Comment,\r\n  CONCAT(e1.Firstname, ' ', e1.Lastname) ActionedBy,\r\n  e.Camp, \r\n  rhist.DateCreated DateActioned\r\nFROM \r\n  RequestDocument rd\r\nLEFT JOIN \r\n(SELECT \r\n    e.Id AS EmployeeID, \r\n    e.Lastname AS LastName, \r\n    e.Firstname AS FirstName, \r\n    c.Description AS Camp\r\nFROM \r\n    Employee e\r\n    LEFT JOIN Room r ON e.RoomId = r.Id\r\n    LEFT JOIN Camp c ON r.CampId = c.Id) e\r\n   ON e.EmployeeID = rd.EmployeeId\r\nLEFT JOIN \r\n  employee ee ON ee.Id = rd.UserIdCreated\r\nLEFT JOIN \r\n  RequestGroupConfig rgc ON rgc.id = rd.AssignedRouteConfigId\r\nLEFT JOIN \r\n  RequestGroup rg ON rgc.GroupId = rg.id\r\nLEFT JOIN (\r\n  SELECT \r\n    rdh.DocumentId, \r\n    MAX(rdh.DateCreated) AS DateCreated,\r\n    rdh.CurrentAction, \r\n    rdh.Comment, \r\n    rdh.ActionEmployeeId\r\n  FROM \r\n    RequestDocumentHistory rdh\r\n  WHERE \r\n    rdh.DateCreated >='{STARTDATE}'  AND\r\n    rdh.DateCreated < '{ENDDATE}' AND\r\n    rdh.CurrentAction = '{ACTION}'\r\n  GROUP BY \r\n    rdh.DocumentId,\r\n    rdh.CurrentAction,\r\n    rdh.Comment,\r\n    rdh.ActionEmployeeId\r\n) rhist ON rd.id = rhist.DocumentId\r\nleft JOIN Employee e1 ON rhist.ActionEmployeeId = e1.Id\r\nWHERE \r\n rd.CurrentAction = '{ACTION}'\r\n {DOCTYPE}";

